import React, {useState} from "react";


export default function TextForm(props) {
    const [text, setText] = useState("");
    
    function handleClick(buttonName) {
        let newText = "";
        if (buttonName === "uppercase") {
          newText = text.toUpperCase();
        } else if (buttonName === "lowercase") {
          newText = text.toLowerCase();
        } else if (buttonName === "removeSpaces") {
          newText = text.split(/[ ]+/).join(" ");
        }
        setText(newText);
        alert(`You have pressed the '${buttonName}' button`);
      }
  
    function handleClick() {
      let newText = text.toUpperCase();
      setText(newText);
    }
  
    function handleClick2() {
      let newText = text.toLowerCase();
      setText(newText);
    }
  
    function handleClick3() {
      let newText = "";
      setText(newText);
    }
  
    function handleClick4() {
      setText(text.split(/[ ]+/).join(" "));
    }
  
    function handleClick5() {
      navigator.clipboard.writeText(text);
    }
  
    function handleOnChange(event) {
      setText(event.target.value);
    }
  
    const isDisabled = !text; // disable buttons if text is empty
    return (
        <>
        <div
          className="container"
          style={{ color: props.theme === "dark" ? "white" : "black" }}
        >
          <h1>{props.heading}</h1>
          <textarea
            className="form-control mb-3"
            id="myBox"
            rows="8"
            value={text}
            style={{
              backgroundColor: props.theme === "dark" ? "#212529" : "white",
              color: props.theme === "dark" ? "white" : "black",
            }}
            onChange={handleOnChange}
          />
          <button
            type="button"
            className="btn btn-primary mx-1 my-1"
            onClick={handleClick}
            disabled={isDisabled}
          >
            Convert to UpperCase
          </button>
          <button
            type="button"
            className="btn btn-primary mx-1 my-1"
            onClick={handleClick2}
            disabled={isDisabled}
          >
            Convert to LowerCase
          </button>
          <button
            type="button"
            className="btn btn-primary mx-1 my-1"
            onClick={handleClick4}
            disabled={isDisabled}
          >
            Remove Spaces
          </button>
          <button
            type="button"
            className="btn btn-primary mx-1 my-1"
            onClick={handleClick5}
            disabled={isDisabled}
          >
            Copy Text
          </button>
          <button
            type="button"
            className="btn btn-primary mx-1 my-1"
            onClick={handleClick3}
            disabled={isDisabled}
          >
            Clear Text
          </button>
        </div>
        <div
          className="container my-3"
          style={{ color: props.theme === "dark" ? "white" : "black" }}
        >
          <h1>Your Text Summary</h1>
          <p>
            {" "}
            {text
              .split(" ")
              .filter((element) => {
                return element.length !== 0;
              }).length}{" "}
            Word and {text.length} Characters
          </p>
          <p> {0.008 * text.split(" ").filter((element) => {
                return element.length !== 0;
              }).length} Minutes Read </p>
          <h2>Preview</h2>
          <p>
            {text.length > 0
              ? text
              : "Enter Your Text Above To Preview It Here"}
          </p>
        </div>
      </>
    );
  }
