import React, {useState} from "react";
import Button from 'react-bootstrap/Button';

export default function TextForm(props) {

    const [text, setText] = useState("");
    const [disableButton, setDisableButton] = useState(true);

    function handleClick() {
        let newText=text.toUpperCase ();
        setText(newText);
        showAlert(`You have pressed the "Convert to UpperCase" button`);
    }

    function handleClick2() {
        let newText=text.toLowerCase ();
        setText(newText);
        showAlert(`You have pressed the "Convert to LowerCase" button`);
    }

    function handleClick3() {
        let newText=("");
        setText(newText);
        showAlert(`You have pressed the "Clear Text" button`);
    }

    function handleClick4() {
        setText(text.split(/[ ]+/).join(" "));
        showAlert(`You have pressed the "Remove Spaces" button`);
    }

    function handleClick5() {
        navigator.clipboard.writeText(text);
        showAlert(`You have pressed the "Copy Text" button`);
    }

    function handleOnChange(event) {
        setText(event.target.value);
        setDisableButton(event.target.value.trim().length === 0);
    }

    function showAlert(message) {
        alert(message);
    }

    return (
        <>
            <div className="container" style={{color:props.theme === 'dark'?'white':'black'}}> 
                <h1>{props.heading}</h1>
                <textarea className="form-control mb-3" id="myBox" rows="8" value={text} style={{backgroundColor: props.theme === 'dark'?'#212529':'white',color:props.theme === 'dark'?'white':'black'}} onChange={handleOnChange}/>
                <Button variant="primary mx-1 my-1" onClick={handleClick} disabled={disableButton}>Convert to UpperCase</Button>
                <Button variant="primary mx-1 my-1" onClick={handleClick2} disabled={disableButton}>Convert to LowerCase</Button>
                <Button variant="primary mx-1 my-1" onClick={handleClick4} disabled={disableButton}>Remove Spaces</Button>
                <Button variant="primary mx-1 my-1" onClick={handleClick5} disabled={disableButton}>Copy Text</Button>
                <Button variant="primary mx-1 my-1" onClick={handleClick3} disabled={disableButton}>Clear Text</Button>
            </div>
            <div className="container my-3" style={{color:props.theme === 'dark'?'white':'black'}}>
                <h1>Your Text Summary</h1>
                <p> {text.split(" ").filter((element)=>{return element.length!==0}).length} Word and {text.length} Characters</p>
                <p> {0.008 * text.split(" ").filter((element)=>{return element.length!==0}).length} Minutes Read </p>
                <h2>Preview</h2>
                <p>{text.length>0?text:"Enter Your Text Above To Preview It Here"}</p>
            </div>
        </>
    )
}
